mapper.select:
SELECT DISTINCT gugunName
FROM dongcode
WHERE sidoName = #{sidoName} AND gugunName IS NOT NULL;

SELECT DISTINCT dongName
FROM dongcode
WHERE gugunName = #{gugunName} AND dongName IS NOT NULL;

SELECT dongcode, sidoName, gugunName, dongName
FROM dongcode
WHERE sidoName = #{sidoName} 
AND gugunName = #{gugunName}
AND dongName = #{dongName}

select info.aptCode,apartmentName, roadName, jibun, area, floor, round(avg(dealAmount),2) as avgPrice, lat, lng 
from (
	select aptCode, apartmentName, roadName, jibun, lat, lng
	from houseinfo
	where dongCode=#{dongcode}
) as info join (
	select aptCode, dealAmount, area, floor
	from housedeal
	group by aptCode, area, floor
) as deal on info.aptCode=deal.aptCode
group by aptCode, roadName, jibun, lat, lng, area, floor
LIMIT #{start},#{listSize}

		select count(*)
		from (
			select aptCode
    		from houseinfo
    		where dongCode=#{dongcode}
		) as info join (
			select distinct aptCode, area, floor
    		from housedeal
		) as deal on info.aptCode=deal.aptCode;

select aptCode, buildYear, roadName, roadNameCode, dong, dongCode, apartmentName, jibun, lng, lat
from houseinfo
where aptCode=#{aptCode}
