mapper.select:
SELECT DISTINCT gugunName
FROM dongcode
WHERE sidoName = #{sidoName} AND gugunName IS NOT NULL;

SELECT DISTINCT dongName
FROM dongcode
WHERE gugunName = #{gugunName} AND dongName IS NOT NULL;

SELECT dongcode, sidoName, gugunName, dongName
FROM dongcode
WHERE sidoName = #{sidoName} 
AND gugunName = #{gugunName}
AND dongName = #{dongName}

select aptCode,dongCode,apartmentName,buildYear, dong, roadName,jibun, lat, lng
from houseinfo
where dongCode=#{dongcode}
LIMIT #{start},#{listSize}

select count(*)
from houseinfo
where dongCode=#{dongcode}

select aptCode, buildYear, roadName, roadNameCode, dong, dongCode, apartmentName, jibun, lng, lat
from houseinfo
where aptCode=#{aptCode}
